import { createSlice } from '@reduxjs/toolkit'

export const games = createSlice({
    name: 'games',
    initialState: {
        game: { 
            directions: 'Go left', 
            question: 'what color i sthe sky?', 
            hint: 'not red', 
            answer: 'blue', }
    },

    reducers: {
        generateGame: (state, action) => {
            fetch('')
                .then(res => res.json())
                .then(data => {
                    console.log(data)
                    state.game = data
                })
        }
    }
})

